  bool access::object_traits_impl< ::TR::Core::IntegrityCheckingPatternData, id_mysql >::
  grow (image_type& i,
        my_bool* t)
  {
    ODB_POTENTIALLY_UNUSED (i);
    ODB_POTENTIALLY_UNUSED (t);

    bool grew (false);

    // m_identity
    //
    if (composite_value_traits< ::TR::Core::IntegrityCheckingIdentity, id_mysql >::grow (
          i.m_identity_value, t + 0UL))
      grew = true;

    // m_pattern
    //
    if (t[3UL])
    {
      i.m_pattern_value.capacity (i.m_pattern_size);
      grew = true;
    }

    return grew;
  }

  void access::object_traits_impl< ::TR::Core::IntegrityCheckingPatternData, id_mysql >::
  bind (MYSQL_BIND* b,
        image_type& i,
        mysql::statement_kind sk)
  {
    ODB_POTENTIALLY_UNUSED (sk);

    using namespace mysql;

    std::size_t n (0);

    // m_identity
    //
    if (sk != statement_update)
    {
      composite_value_traits< ::TR::Core::IntegrityCheckingIdentity, id_mysql >::bind (
        b + n, i.m_identity_value, sk);
      n += 3UL;
    }

    // m_pattern
    //
    b[n].buffer_type = MYSQL_TYPE_BLOB;
    b[n].buffer = i.m_pattern_value.data ();
    b[n].buffer_length = static_cast<unsigned long> (
      i.m_pattern_value.capacity ());
    b[n].length = &i.m_pattern_size;
    b[n].is_null = &i.m_pattern_null;
    n++;
  }

  void access::object_traits_impl< ::TR::Core::IntegrityCheckingPatternData, id_mysql >::
  bind (MYSQL_BIND* b, id_image_type& i)
  {
    std::size_t n (0);
    mysql::statement_kind sk (mysql::statement_select);
    composite_value_traits< ::TR::Core::IntegrityCheckingIdentity, id_mysql >::bind (
      b + n, i.id_value, sk);
  }

  bool access::object_traits_impl< ::TR::Core::IntegrityCheckingPatternData, id_mysql >::
  init (image_type& i,
        const object_type& o,
        mysql::statement_kind sk)
  {
    ODB_POTENTIALLY_UNUSED (i);
    ODB_POTENTIALLY_UNUSED (o);
    ODB_POTENTIALLY_UNUSED (sk);

    using namespace mysql;

    bool grew (false);

    // m_identity
    //
    if (sk == statement_insert)
    {
      ::TR::Core::IntegrityCheckingIdentity const& v =
        o.m_identity;

      if (composite_value_traits< ::TR::Core::IntegrityCheckingIdentity, id_mysql >::init (
            i.m_identity_value,
            v,
            sk))
        grew = true;
    }

    // m_pattern
    //
    {
      ::TR::Core::Blob const& v =
        o.m_pattern;

      bool is_null (false);
      std::size_t size (0);
      std::size_t cap (i.m_pattern_value.capacity ());
      mysql::value_traits<
          ::TR::Core::Blob,
          mysql::id_blob >::set_image (
        i.m_pattern_value,
        size,
        is_null,
        v);
      i.m_pattern_null = is_null;
      i.m_pattern_size = static_cast<unsigned long> (size);
      grew = grew || (cap != i.m_pattern_value.capacity ());
    }

    return grew;
  }

